DataSet.ClientEvent:
if(!self.get("data").getLast()) {
	//dorado.MessageBox.alert("没有找到满足条件的设计，请重新限定条件查询...");
	dorado.widget.NotifyTipManager.notify("没有找到满足条件的设计，请重新限定条件查询...");
}

AutoFormElement.ClientEvent:
//获取按键对应的keyCode
var keyCode = arg.keyCode;
//如果是回车键则直接执行Action操作

//alert(self.get("value"));
if(!self.get("value")){
	view.get("#autoformCondition").get("entity").set("code",keyCodeToValue(keyCode));
	view.get("#dsClamparmlib").set("parameter", view.get("#autoformCondition").get("entity")).flushAsync();
}else{
	if(keyCode == 8){
		view.get("#autoformCondition").get("entity").set("code",self.get("value").substring(0,self.get("value").length-1));
		view.get("#dsClamparmlib").set("parameter", view.get("#autoformCondition").get("entity")).flushAsync();		
	}else{
		var temp = self.get("value");
		view.get("#autoformCondition").get("entity").set("code",temp + keyCodeToValue(keyCode));
	//self.refresh();
	//alert(view.get("#autoformCondition").get("entity").get("code"));
		view.get("#dsClamparmlib").set("parameter", view.get("#autoformCondition").get("entity")).flushAsync();		
		
	}
}

//实现keyCode值得转换
function keyCodeToValue(keyCode){
	switch(keyCode){
		case 96:
		case 48:return 0+"";
		case 97:
		case 49:return 1+"";
		case 98:
		case 50:return 2+"";
		case 99:
		case 51:return 3+"";
		case 100:
		case 52:return 4+"";
		case 101:
		case 53:return 5+"";
		case 102:
		case 54:return 6+"";
		case 103:
		case 55:return 7+"";
		case 104:
		case 56:return 8+"";
		case 105:
		case 57:return 9+"";
		
		
		case 65:return "A";
		case 66:return "B";
		case 67:return "C";
		case 68:return "D";
		case 69:return "E";
		case 70:return "F";
		case 71:return "G";
		case 72:return "H";
		case 73:return "I";
		case 74:return "J";
		case 75:return "K";
		case 76:return "L";
		case 77:return "M";
		case 78:return "N";
		case 79:return "O";
		case 80:return "P";
		case 81:return "Q";
		case 82:return "R";
		case 83:return "S";
		case 84:return "T";
		case 85:return "U";
		case 86:return "V";
		case 87:return "W";
		case 88:return "X";
		case 89:return "Y";
		case 90:return "Z";


		default:
			dorado.widget.NotifyTipManager.notify("非正常输入，请三思而输！！系统为您作出修正！！！");
			return "";
	}
}

ToolBarButton.ClientEvent:
//获取autoformCondition绑定的实体对象
var entity = autoformCondition.get("entity");
//将实体对象作为参数传入，并异步刷新
dsClamparmlib.set("parameter",entity).flushAsync();

AjaxAction.ClientEvent:
//得到返回值
var result = self.get("returnValue");
//传参成功后执行downloadActionFile
downloadCatiaModel.set("parameter",result);
downloadCatiaModel.execute();

//得到返回值
var result = self.get("returnValue");
//传参成功后执行downloadActionFile
downloadDrawing.set("parameter",result);
downloadDrawing.execute();
