/**
 * 保存前校验钳臂所属系列输入格式是否规范
 */
var armSerial=String(autoformTurretarmInfo.get("entity").get("armSerial"));
if(armSerial.length>3||armSerial.substr(1,1)!="-"){
	dorado.MessageBox.alert("钳臂所属系列格式应该符合'x-x'格式！");
}else if(parseInt(armSerial.substr(0,1))<1||parseInt(armSerial.substr(2,1))<1||isNaN(armSerial.substr(0,1))||isNaN(armSerial.substr(2,1))){
	dorado.MessageBox.alert("'x-x'格式中'x'必须是大于0的整数！");
}else if(parseInt(armSerial.substr(0,1))>5||parseInt(armSerial.substr(2,1))>3){
	dorado.MessageBox.alert("超出图号范围！");
}else{
	
	
