if(armAngel4==null){
	armAngel4=0;
}else{
	armAngel4=parseInt(String(armAngel4));
}

if(armAngel4==0){
	armAngel4Code="0";
}else if(armAngel4<0){
	armAngel4Code="1";
}else if(armAngel4>0){
	armAngel4Code="2";
}

/*shankType-----------------------------------------------------------------------------------*/
var shankType=autoformTurretarmInfo.get("entity").get("shankType");
var shankTypeCode="";

switch(shankType){
	case '螺纹式':
		shankTypeCode="0";
	break;
	case '抱紧式':
		shankTypeCode="1";
	break;
	case '其他':
