updateactionSave.execute();

//datagridTurretarm.set("parameter",entity).flushAsync();
}

// 如果是修改：
if (dialogEdit.get("tags") == "Modify") {
	dataSetTurretarm.get("data:#").cancel();
	dialogEdit.hide();
	view.id("updateactionSave").set("userData",null);
}
// 如果是添加：
if (dialogEdit.get("tags") == "Add") {
	var entity = dataSetTurretarm.get("data:#");
