		case 'Φ54/CuCr1Zr':
			view.get("#armLength1").set("trigger","listdropdownArmLength1Cu");
			view.get("#armLength2").set("trigger","listdropdownArmLength2Cu");
		break;
		case '100×40/7075(T6)':
			view.get("#armLength1").set("trigger","listdropdownArmLength1Al");
